import { Button, Card, Container } from "react-bootstrap";

export const GameCard = ({ cover, name, summary }) => {
  return (
    <Card
      className="hover-shadow"
      style={{
        width: "20rem",
        height: "35rem",
        backgroundColor: "#001124",
        margin: "1rem",
      }}
    >
      {cover && (
        <Card.Img variant="top" src={cover} style={{ height: "25rem" }} />
      )}
      <Card.Body>
        <Container>
          <Row>
            <Card.Title>
              {name.length > 40 ? `${name.slice(0, 40)}...` : name}
            </Card.Title>
          </Row>
          <Row>
            <Button variant="primary" style={{ alignItems: "end" }}>
              Details
            </Button>
          </Row>
        </Container>
        {/* <Card.Text>{summary?.slice(0, 100)}</Card.Text>
        <div className="d-flex h-100 justify-content-end align-items-end bg-danger">
        </div> */}
      </Card.Body>
    </Card>
  );
};
