import { Button, Card } from "react-bootstrap";

export const GameCard = ({ cover, name, summary }) => {
  return (
    <Card
      className="hover-shadow"
      style={{
        width: "20rem",
        height: "35rem",
        backgroundColor: "#001124",
        margin: "1rem",
      }}
    >
      {cover && (
        <Card.Img variant="top" src={cover} style={{ height: "10rem" }} />
      )}
      <Card.Body>
        <Card.Title>
          {name.length > 40 ? `${name.slice(0, 40)}...` : name}
        </Card.Title>
        {/* <Card.Text>{summary?.slice(0, 100)}</Card.Text> */}
        <div className="d-flex h-75 justify-content-end align-items-end">
          <Button variant="primary" style={{ alignItems: "end" }}>
            Details
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
};
