import { useEffect, useState } from "react";
import { Button, Carousel, Col, Container, Image, Row } from "react-bootstrap";
import { useNavigate, useSearchParams } from "react-router-dom";
import { gameDetails } from "../Controllers/igdb";
import { Footer } from "./Components/Footer";
import { Header } from "./Components/Header";

export const GamePage = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const query = searchParams.get("id");
  const [searchInput, setSearchInput] = useState("");
  const [game, setGame] = useState([]);
  const [searchQuery, setSearchQuery] = useState(query);

  useEffect(() => {
    (async () => {
      const response = await gameDetails(searchQuery);
      setGame(response);
    })();
  }, [searchQuery]);

  useEffect(() => {
    setSearchInput("");
    setSearchQuery(query);
  }, [query]);

  return (
    <div className="d-flex flex-column min-vh-100 w-100">
      <Header />
      <Container className="mb-5">
        <Row className="w-100 justify-content-center">
          <Col className="col-10">
            <div className="active-cyan-4 mb-4">
              <input
                className="form-control"
                type="text"
                placeholder="Search for a game..."
                aria-label="Search"
                onChange={(e) => {
                  setSearchInput(e.target.value);
                }}
                value={searchInput}
                onKeyUp={(e) => {
                  if (e.key === "Enter") {
                    if (searchInput.length) {
                      setSearchQuery(searchInput);
                      navigate(`/search?q=${searchInput}`);
                    }
                  }
                }}
              />
            </div>
          </Col>
          <Col className="col-1">
            <Button
              onClick={() => {
                if (searchInput === query) return;
                setSearchQuery(searchInput);
                navigate(`/search?q=${searchInput}`);
              }}
            >
              Search
            </Button>
          </Col>
        </Row>
        <Row>
          {game.artworks && (
            <Carousel className="mb-5">
              {game.artworks.map((art, index) => (
                <Carousel.Item
                  key={index}
                  style={{
                    height: "20em",
                  }}
                >
                  <div className="d-flex justify-content-center">
                    <Image
                      fluid={true}
                      src={`https://images.igdb.com/igdb/image/upload/t_1080p/${art}`}
                    ></Image>
                  </div>
                </Carousel.Item>
              ))}
            </Carousel>
          )}
        </Row>
        <Row className="w-100 d-flex justify-content-center">
          <Col className="col-3">
            <Row>
              {game.cover && (
                <Image
                  src={`https://images.igdb.com/igdb/image/upload/t_cover_big/${game.cover}`}
                  className="w-100"
                ></Image>
              )}
            </Row>
            <Row>
              <div className="mt-2">
                <h6>Developed by:</h6>
                {game.involved_companies && game.involved_companies.join(", ")}
              </div>
            </Row>
            {/* <Row style={{ paddingInline: 12 }}>{game.themes?.join(", ")}</Row>
            <Row>
              <p>{`Ratings: ${
                game.aggregated_rating
                  ? Number(game.aggregated_rating).toFixed(0) + "%"
                  : "No ratings found"
              }`}</p>
            </Row> */}
          </Col>
          <Col>
            <Row>
              <div className="d-flex justify-content-between align-items-center">
                <h1>{game.name}</h1>
                <h3>
                  <b>Ratings: </b>
                  {`${
                    game.aggregated_rating
                      ? Number(game.aggregated_rating).toFixed(0) + "%"
                      : "N/A"
                  }`}
                </h3>
              </div>
            </Row>
            <Row>
              <div className="d-flex mb-2">
                {game.themes?.map((item, index) => (
                  <Button
                    disabled={true}
                    className="me-2"
                    size="sm"
                    variant="outline-info"
                    key={index}
                  >
                    {item}
                  </Button>
                ))}
              </div>
              {/* <p>{game.themes?.join(", ")}</p> */}
            </Row>
            <Row>
              <p>{game.summary}</p>
              <p>
                {game.storyline && game.storyline.length > 500
                  ? `${game.storyline.slice(0, 500)}...`
                  : game.storyline}
              </p>
            </Row>
          </Col>
        </Row>
      </Container>
      <Footer />
    </div>
  );
};
