import { useEffect, useState } from "react";
import { Button, Alert, Row, Container, Col, Card } from "react-bootstrap";
import { getCover, searchGames } from "../Controllers/igdb";
import {
  Navigate,
  useLocation,
  useNavigate,
  useSearchParams,
} from "react-router-dom";
import { GameCard } from "./Components/GameCard";

export const SearchPage = () => {
  const coverUrl = "https://images.igdb.com/igdb/image/upload/t_cover_big/";

  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();
  const query = searchParams.get("q");
  const [gameList, setGameList] = useState([]);
  const [tempCov, setCov] = useState();
  const fetchCover = async (id) => {
    return await getCover(id);
  };

  useEffect(() => {
    (async () => {
      const response = await searchGames(query);
      response.map((item) => {
        getCover(item.id).then((data) => {
          setGameList((old) => [
            ...old,
            {
              id: item.id,
              name: item.name,
              summary: item.summary,
              cover: `${coverUrl}${data}`,
            },
          ]);
        });
        // console.log("not over yet");
      });
      // console.log(list);
      // setGameList(list);
    })();
  }, []);

  return (
    <div className="w-100 vh-100">
      <Container>
        <Row className="align-items-center d-flex">
          {gameList.length &&
            gameList.map((item) => (
              <GameCard
                key={item.id}
                cover={item.cover}
                name={item.name}
                summary={item.summary}
              />
            ))}
          {/* <Card style={{ width: "18rem" }}>
            <Card.Img variant="top" src={gameList?.at(0)?.cover} />
            <Card.Body>
              <Card.Title>{gameList?.at(0)?.name}</Card.Title>
              <Card.Text>{gameList?.at(0)?.summary}</Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card> */}
        </Row>
      </Container>
    </div>
  );
};
