import { useEffect, useState } from "react";
import { Button, Alert, Row, Container, Col } from "react-bootstrap";
import { getCover, searchGames } from "../Controllers/igdb";
import {
  Navigate,
  useLocation,
  useNavigate,
  useSearchParams,
} from "react-router-dom";

export const SearchPage = () => {
  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();
  const query = searchParams.get("q");
  const [gameList, setGameList] = useState();
  useEffect(() => {
    (async () => {
      const cover = await getCover("1020");
      const response = await searchGames(query);
      setGameList(response);
    })();
  }, []);

  return (
    <div className="w-100 vh-100">
      <Container>
        <Row className="align-items-center d-flex">
          <h1>{query}</h1>
          {gameList && <p>{JSON.stringify(gameList)}</p>}
        </Row>
      </Container>
    </div>
  );
};
