import { Button, Card } from "react-bootstrap";

export const GameCard = ({ cover, name, summary }) => {
  return (
    <Card
      className="hover-shadow"
      style={{
        width: "20rem",
        height: "40rem",
        backgroundColor: "#001124",
        margin: "1rem",
      }}
    >
      {cover && <Card.Img variant="top" src={cover} />}
      <Card.Body>
        <Card.Title>
          {name.length > 20 ? `${name.slice(0, 20)}...` : name}
        </Card.Title>
        {/* <Card.Text>{summary?.slice(0, 100)}</Card.Text> */}
        <Button variant="primary" style={{ alignItems: "end" }}>
          Details
        </Button>
      </Card.Body>
    </Card>
  );
};
