import { Card } from "react-bootstrap";

export const GameCard = ({ cover, name, summary }) => {
  return (
    <Card
      className="hover-shadow"
      key={item.id}
      style={{
        width: "20rem",
        backgroundColor: "#001124",
        margin: "1rem",
      }}
    >
      {item.cover && <Card.Img variant="top" src={item.cover} />}
      <Card.Body>
        <Card.Title>{item.name}</Card.Title>
        <Card.Text>{item.summary.slice(0, 100)}</Card.Text>
        <Button variant="primary" style={{ alignItems: "end" }}>
          Details
        </Button>
      </Card.Body>
    </Card>
  );
};
