import { useEffect, useState } from "react";
import { Button, Alert, Row, Container, Col, Card } from "react-bootstrap";
import { gameDetails, getCover, searchGames } from "../Controllers/igdb";
import {
  Navigate,
  useLocation,
  useNavigate,
  useSearchParams,
} from "react-router-dom";
import { GameCard } from "./Components/GameCard";

export const GamePage = () => {
  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();
  const query = searchParams.get("id");
  const [searchInput, setSearchInput] = useState("");
  const [game, setGame] = useState([]);
  const [searchQuery, setSearchQuery] = useState(query);

  useEffect(() => {
    (async () => {
      const response = await gameDetails(searchQuery);
      setGame(response);
    })();
  }, [searchQuery]);

  useEffect(() => {
    setSearchInput("");
    setSearchQuery(query);
  }, [query]);

  return (
    <div className="w-100 vh-100">
      <Container>
        <Row className="w-100">
          <Col className="col-10">
            <div className="active-cyan-4 mb-4">
              <input
                className="form-control"
                type="text"
                placeholder="Search for a game..."
                aria-label="Search"
                onChange={(e) => {
                  setSearchInput(e.target.value);
                }}
                value={searchInput}
                onKeyUp={(e) => {
                  if (e.key === "Enter") {
                    if (searchInput === query) return;
                    setSearchQuery(searchInput);
                    navigate(`/search?q=${searchInput}`);
                  }
                }}
              />
            </div>
          </Col>
          <Col>
            <Button
              onClick={() => {
                if (searchInput === query) return;
                setSearchQuery(searchInput);
                navigate(`/search?q=${searchInput}`);
              }}
            >
              Search
            </Button>
          </Col>
        </Row>
        <Row className="w-100">
          <Col>
            <Row>
              <p>{game.aggregated_rating}</p>
            </Row>
            <Row>{game.themes?.join(", ")}</Row>
          </Col>
          <Col>
            <Row>
              <p>{game.name}</p>
            </Row>
            <Row>
              <p>{game.summary}</p>
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
};
